import React, { Component } from 'react'
import { Form, Button, Row, Col } from 'react-bootstrap'
import BookCard from '../BookCard'
import './style.css';
import API from '../../utils/API';

export default class Booklist extends Component {

    state = {
        userInput: '',
        searchResults: []
    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(this.state.userInput);
        API.search(this.state.userInput)
            .then(res => this.setState({
                searchResults: res.data.items,
                userInput: ""
            }))
    }

    handleInputChange = event => {

        this.setState({ userInput: event.target.value });
    }

    renderBooks = () => {
        if (!this.state.searchResults) {
            return <h1>Search for a book to start!</h1>
        } else {
            return <div>
                {this.state.searchResults.map((book, index) => <BookCard
                    authors={book.volumeInfo.authors}
                    imgURL={book.volumeInfo.imageLinks.thumbnail}
                    title={book.volumeInfo.title}
                    subTitle={book.volumeInfo.subtitle}
                    infoLink={book.volumeInfo.infoLink}
                    description={book.volumeInfo.description}
                    bookID={book.id}
                    key={book.id} />)}
            </div>
        }

    }

    render() {
        return (
            <div>

                <div className="search-form-container">

                    <form onSubmit={this.handleSubmit}>

                        <Form.Group>
                            <Row>
                                <Col xs={12}>
                                    <Form.Control type="text" placeholder="Book Title..." onChange={this.handleInputChange} id="book-input" />
                                </Col>
                            </Row>
                            <Row>
                                {/* <Col xs={10}></Col> */}
                                <Col>
                                    <Button variant="primary" type="submit">
                                        Submit
                                    </Button>
                                </Col>
                            </Row>
                        </Form.Group>
                    </form>

                </div>
                <div className="books-container">
                    {this.renderBooks()}
                    {/* <div>
                        {this.state.searchResults.map((book, index) => <BookCard
                            authors={book.volumeInfo.authors}
                            imgURL={book.volumeInfo.imageLinks.thumbnail}
                            title={book.volumeInfo.title}
                            subTitle={book.volumeInfo.subtitle}
                            infoLink={book.volumeInfo.infoLink}
                            description={book.volumeInfo.description}
                            bookID={book.id}
                            key={book.id} />)}
                    </div> */}
                </div>
            </div>


        )
    }
}
